  const title = '那只猫喝咖啡的分享！'
  console.log('进入')
  switch (type) {
    case 'qq':
      toQQ(url, title)
      break
    case 'QQzone':
      toQQzone(url, title)
      break
    case 'WeiBo':
      toWeibo(url, title)
      break
    case 'WeiXin':
      toWechat(url, title)
      break
    default:
      this.$message.error('出错了！')
      break
  }
}
